import styles from "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css";
import {
  MainContainer,
  ChatContainer,
  MessageList,
  Message,
  MessageInput,
  ConversationHeader,
  // Avatar,
} from "@chatscope/chat-ui-kit-react";
import Avatar from "@mui/material/Avatar";
import { Grid, Paper } from "@mui/material";
import React from "react";
import { Box } from "@mui/system";

function Chat() {
  return (
    <div>
      <Paper square="true" sx={{ flexGrow: 1 }}>
        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
      </Paper>
      <Box
        sx={{
          maxWidth: 350,
          // margin: 0.5,
          width: 1,
          // border: 1,
          // p: 1,
          // display: "block",
        }}
      >
        {/* <ConversationHeader>
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
          <ConversationHeader.Content>
            <span
              style={{
                color: "#ec1212",
                alignSelf: "flex-center",
              }}
            >
              Custom content
            </span>
          </ConversationHeader.Content>
        </ConversationHeader> */}

        <Grid container justifyContent="flex-end">
          <Message
            model={{
              message: "Hello my friend",
              sentTime: "just now",
              sender: "Joe",
            }}
          />
          <Message
            model={{
              message: "Hello my friend",
              sentTime: "just now",
              sender: "Akane",
              direction: "outgoing",
              position: "single",
            }}
          />
        </Grid>
        <Message
          model={{
            message: "Hello my friend",
            sentTime: "just now",
            sender: "Joe",
          }}
        />
        <Grid container justifyContent="flex-end">
          <Message
            model={{
              message: "Hello my friend",
              sentTime: "just now",
              sender: "Joe",
            }}
          />
        </Grid>
      </Box>
    </div>
  );
}

export default Chat;
